import pytest
from src.practice.python import merge_the_tools


@pytest.mark.parametrize(
    's,k,expected',
    [
        (
            'AABCAAADA',
            3,
            ['AB',
             'CA',
             'AD']
        ),
        (
            '',
            3333,
            ['UWFDSVEPZHBAQTXGRMNCYLJIKO',
             'LROTJNQKHAEIUGZDWSBXCVMYPF',
             'PLAUVDWFYKHRNJOMEXIZQSGCBT']
        )
    ]
)
def test_merge_the_tools(s, k, expected):
    assert merge_the_tools(s, k) == expected
